var React = require("react/addons");
var AppActions = require('app/actions/app-actions');

var Catalog = require('app/components/catalog/app-catalog.jsx');
var CatalogItem = require('app/components/catalog/app-catalogitem.js');
var Cart = require('app/components/cart/app-cart.jsx');

var Template = require('app/components/app-template.js');
var Router = require('react-router-component');

var Locations = Router.Locations;
var Location = Router.Location;

var App = React.createClass({
    render: function () {
        return (
            <Template>
                <Locations>
                    <Location path="/" handler={Catalog} />
                    <Location path="/cart" handler={Cart} />
                    <Location path="/item/:item" handler={CatalogItem} />
                </Locations>
            </Template>
        );
    },
});

module.exports = App;
